mapper.insert:
INSERT INTO personallog.article (
    title, content, summary, category_id, user_id,
    view_count, is_top, status
) VALUES (
             #{title}, #{content}, #{summary}, #{categoryId}, #{authorId},
             #{viewCount}, #{isTop}, #{status}
         )

mapper.update:
UPDATE personallog.article
title = #{title}, content = #{content}, summary = #{summary}, category_id = #{categoryId}, is_top = #{isTop}, status = #{status}, update_time = NOW()
WHERE id=#{id}

mapper.select:
SELECT
a.*,
c.name as category_name,
u.username,
u.avatar
FROM personallog.article a
LEFT JOIN personallog.category c ON a.category_id = c.id
LEFT JOIN personallog.user u ON a.user_id = u.id
WHERE 1=1

    AND a.title LIKE CONCAT('%', #{title}, '%')


    AND a.category_id = #{categoryId}


    AND a.user_id = #{authorId}


    AND a.status = #{status}


    AND a.is_top = #{isTop}


    AND a.create_time >= #{startTime}


    AND a.create_time <= #{endTime}

ORDER BY

    a.update_time DESC
    a.view_count DESC
    a.create_time DESC


    LIMIT #{offset}, #{pageSize}

SELECT COUNT(*) FROM personallog.article
WHERE 1=1

    AND title LIKE CONCAT('%', #{title}, '%')


    AND category_id = #{categoryId}


    AND user_id = #{authorId}


    AND status = #{status}


    AND is_top = #{isTop}


    AND create_time >= #{startTime}


    AND create_time <= #{endTime}
