mapper.update:
UPDATE personallog.comment
SET status = 0, update_time = NOW()
WHERE id = #{commentId}

UPDATE personallog.comment
content = #{content}, status = #{status}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT
    c.id, c.user_id, c.article_id, c.content,
    c.parent_id, c.status, c.create_time, c.update_time
FROM personallog.comment c
WHERE c.status != 0
  AND (
    c.article_id IN (
        SELECT id FROM personallog.article
        WHERE user_id = #{userId}
    )
        OR
    c.parent_id IN (
        SELECT id FROM personallog.comment
        WHERE user_id = #{userId}
    )
    )
ORDER BY c.create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) > 0
FROM personallog.comment
WHERE id = #{commentId}
  AND user_id = #{userId}
  AND status != 0

SELECT COUNT(*) > 0
FROM personallog.comment
WHERE id = #{commentId}
  AND article_id = #{articleId}
  AND status != 0

SELECT COUNT(*)
FROM personallog.comment c
WHERE c.status != 0
  AND (
    c.article_id IN (
        SELECT id FROM personallog.article
        WHERE user_id = #{userId}
    )
        OR
    c.parent_id IN (
        SELECT id FROM personallog.comment
        WHERE user_id = #{userId}
    )
    )
  AND c.user_id != #{userId}

mapper.update:
UPDATE personallog.comment
SET status = 1, update_time = NOW()
WHERE id = #{commentId}
